on of prostate cancer will\nreverse current unfavourable trends and ultimately save lives.	[0.0034692923, 0.04281372, -0.1605892, -0.01949688, 0.052624848, 0.0477942, 0.0070444797, 0.04065058, 0.03503144, -0.011824194, 0.05561468, 0.04937761, 0.053616416, -0.022004029, -0.015975805, -0.019736923, -0.0041959914, -0.04632168, -0.053817198, 0.013933253, -0.033147886, -0.07298996, -0.032588452, -0.024657086, 0.07088145, 0.019724818, -0.035717618, -0.031077608, -0.053016257, 0.0038631514, 0.10708078, -0.019731311, 0.016754843, -0.027946226, 0.011010096, -0.06870517, 0.07128975, 0.015144133, 0.029440457, -0.029901449, 0.016783671, -0.0056447447, 0.0043988177, -0.05028159, 0.023068292, -0.006287661, -0.035787556, 0.026951022, 0.023348588, -0.075898305, 0.025815586, -0.025360657, 0.015422117, 0.013737001, 0.03922989, -0.013261972, -0.03446201, 0.0124087315, 0.03174823, -0.07257862, 0.09330538, 0.039052717, -0.075840265, 0.022237867, 0.07995237, -0.045870993, 0.02321538, 0.015690481, 0.025616061, 0.015404818, -0.01462172, -0.026908364, 0.049760558, 0.0027045065, 0.0072541176, 0.019527314, -0.023252986, -0.030350655, 0.009838214, 0.021443574, 0.03677018, 0.0059687565, 0.043548275, -0.027994834, -0.007481569, -0.034526445, 0.029032556, -0.021482361, -0.008241436, 0.081063, 0.037835065, 0.007014189, 0.02695228, 0.065990746, -0.010362959, 0.032946866, -0.017101789, -0.015557434, -0.040830035, 0.025067104, -0.025635967, -0.011740511, -0.06802691, 0.01750051, -0.027524047, 0.04987517, 0.0011739299, 0.008966641, -0.008283888, 0.0055781296, -0.01498406, 0.068312086, -0.056501515, -0.02099229, 0.00881914, 0.027309358, 0.08678912, -0.010711994, 0.05230678, 0.047361124, 0.0011945884, -0.006582566, 0.04707367, 0.0921078, -0.01552637, 0.03661703, -0.06592845, -0.028062709, 0.015244159, 0.0054009994, -0.013401028, -0.03834194, -0.038488608, 0.009946675, 0.012587974, 0.07476043, -0.007537834, -0.024867134, 0.025521597, 0.022635117, 0.0012727493, 0.037590645, 0.026121406, -0.014381031, -0.0